data.logic:
var calc_output = [];

var a = parseFloat(age);
var lab = parseFloat(lab) || 0;
var type = parseFloat(type);

var score, unit, msg1, msg2, msg;

var score1 = a*10;
var score2 = a*5;
var unit1 = 'µg/L';
var feu = 'Age-adjusted D-dimer cutoff, FEU';
var ddu = 'Age-adjusted D-dimer cutoff, DDU';

if (type == 0) {
  score = a*10;
  msg = 'Age-adjusted D-dimer cutoff, FEU';
} else {
  score = a*5;
  msg = 'Age-adjusted D-dimer cutoff, DDU';
}

if (UOMSYSTEM == true) {
  if (lab > score) {
    msg1 = 'VTE possible';
    msg2 = 'Reported D-dimer is greater than cutoff; consider confirmatory testing with CTA or V/Q scan';
  } else {
  	msg1 = 'VTE unlikely';
  	msg2 = 'Reported D-dimer is less than or equal to cutoff; consider alternative diagnosis';
  }
  unit = 'µg/L';
}
if (UOMSYSTEM == false) {
  score = score/1000;
  if (lab > score) {
    msg1 = 'VTE possible';
    msg2 = 'Reported D-dimer is greater than cutoff; consider confirmatory testing with CTA or V/Q scan';
  } else {
  	msg1 = 'VTE unlikely';
  	msg2 = 'Reported D-dimer is less than or equal to cutoff; consider alternative diagnosis';
  }
  unit = 'µg/mL';
}

var unittype
if (type == 0) {
	unittype = 'FEU'
}
if (type == 1) {
	unittype = 'DDU'
}

//results
if (lab !== 0) { // user enters a level
calc_output.push({
    name: 'mini',
    value: msg1,
    value_text: '',
    message: score + ' ' + unit + ' ' + unittype + ' cutoff'
});
calc_output.push({
    name: '10138_Age-Adjusted D-dimer_result',
    value: score,
    value_text: unit,
    message: msg 
    });
calc_output.push({
    name: '10138_Age-Adjusted D-dimer_interpretation',
    value: msg1, 
    value_text: '',
    message: msg2
});
}
if (lab == 0) { // user doesn't enter a level
calc_output.push({
    name: 'mini',
    value: score1,
    value_text: unit1,
    message: feu
});
calc_output.push({
    name: '10138_Age-Adjusted D-dimer_result (FEU)',
    value: score1,
    value_text: unit1,
    message: feu 
    });
calc_output.push({
    name: '10138_Age-Adjusted D-dimer_result (DDU)',
    value: score2,
    value_text: unit1,
    message: ddu 
    });
}
