data.logic:
var other = other || 0;
var syndrome = syndrome || 0;
var presence = presence || 0;

var calc_output = [];
var other = parseFloat(other);
var presence = parseFloat(presence);
var syndrome = parseFloat(syndrome);
var histo = parseFloat(histo);
var absence = parseFloat(absence);
var onset = parseFloat(onset);
var disturbance = parseFloat(disturbance);
var memory = parseFloat(memory);
var deficits = parseFloat(deficits);
var tests = parseFloat(tests);

var probable = tests + deficits + memory + disturbance + onset + absence;
var definite = histo;
var possible = other + presence + syndrome;

var diag, risk, msg;

if ((probable + definite) == 7) {
  diag = "Definite AD";
  risk = "";
  msg = "";
} else if (probable == 6) {
  diag = "Probable AD";
  risk = "91";
  msg = "% rate of AD by pathological diagnosis";
} else if (possible == 3) {
  diag = "Possible AD";
  risk = "92";
  msg = "% rate of AD by pathological diagnosis";
} else {
  diag = "Criteria not met";
  risk = "";
  msg = "";
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: risk + msg
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: risk + msg   
    });
