var calc_output = [];
var time = parseFloat(time);
var number = parseFloat(number);

number = Math.round(number);
var score = number/time;
score = score.toFixed(1);

var risk, os; 

if (score < 4) {
  risk = 'Low';
  os = '12.4';
} else if (score >= 4 && score <= 13) {
  risk = 'Intermediate';
  os = '8.2';
} else if (score > 13) {
  risk = 'High';
  os = '4.3';
}

//results
