data.logic:
var calc_output = [];
var depressed = parseFloat(depressed);
var ideation = parseFloat(ideation);
var attempt = parseFloat(attempt);
var when = parseFloat(when);

var msg = ''; 
var mini_msg = ''; 

if (depressed == 1){
  msg += '<br/>Depressed mood';
  mini_msg = 'Depressed mood';
} else {
  msg += '';
}

if (ideation == 1){
  msg += '<br/>Active suicidal ideation';
  mini_msg = 'Active suicidal ideation';  
} else {
  msg += '';
}

if (attempt == 1){
	if (when < 3) {
		msg += '<br/>Recent suicide attempt';
        mini_msg = 'Recent suicide attempt';  
	} else if (when >= 3) {
		msg += '<br/>Lifetime suicide attempt';
        mini_msg = 'Lifetime suicide attempt';  
	}
} else {
	msg += '';
}

var screen;

if (ideation == 1 || attempt == 1) {
	screen = 'Positive';
} else {
	screen = 'Not positive';
}

//results
calc_output.push({
    name: 'mini',
    value: screen,
    value_text: '',
    message: mini_msg
});
calc_output.push({
    name: 'Score',
    value: screen,
    value_text: '',
    message: screen + ' for suicidality by ED-SAFE PSS-3'   
});
if (depressed ==1 || attempt ==1 || ideation ==1) {
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: 'Patient is presenting with:'+msg
});
}
