data.logic:
var calc_output = [];
var acute = parseFloat(acute);
var unplanned = parseFloat(unplanned);
var bmi = parseFloat(bmi);

var score = bmi + unplanned + acute;

var risk;
var rec;

if (score == 0) {
  risk = 'Low';
  rec = 'Routine clinical care';
} else if (score == 1) {
  risk = 'Medium';
  rec = 'Observe; document dietary intake and if adequate/little concern, continue screening; if inadequate, improve and increase overall intake according to local policy';
} else if (score >= 2) {
  risk = 'High';
  rec = 'Treat (unless detrimental or no benefit is expected from nutritional support, e.g. imminent death); refer to dietitian, improve and increase overall intake, increase frequency of monitoring according to local policy';
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' overall malnutrition risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'MUST Score'  
});
calc_output.push({
    name: 'Score',
    value: risk,
    value_text: 'risk',
    message: rec   
});
