data.logic:
var calc_output = [];
var Cr = parseFloat(Cr);
var Cr1 = parseFloat(Cr1);
var Cr2 = parseFloat(Cr2);
var t = parseFloat(t);
var max = 1.5;
var age = parseFloat(age);
var race = parseFloat(race) || -1;
var sex = parseFloat(sex);

if (UOMSYSTEM == false) {
  Cr1 = Cr1/88.4;
  Cr2 = Cr2/88.4;
  Cr = Cr/88.4;
}
if (UOMSYSTEM == true) {
  Cr1 = Cr1;
  Cr2 = Cr2;
  Cr = Cr;
}

if (sex == 1) {
  female = 0.742;
} else {
  female = 1;
}

if (race == 1) {
  black = 1.212;
} else if (race == 2) {
  black = 1;
} else if (race == -1) {
  black = 1.212;
}

var eGFR = 186*Math.pow(Cr,-1.154)*Math.pow(age,-0.203)*black*female;

var a = (Cr*eGFR) / ((Cr1 + Cr2) / 2);
var b = 1 - ((24*(Cr2 - Cr1)) / (t*max));
if (b > 1) {
  b = 1;
} else if (b < 0) {
  b = 0;
}

var kGFR = a*b;

kGFR = kGFR.toFixed(1);

//race changes
if (race == -1) {
  var eGFR_1 = 186*Math.pow(Cr,-1.154)*Math.pow(age,-0.203)*1*female;

  var a_1 = (Cr*eGFR_1) / ((Cr1 + Cr2) / 2);
  var b_1 = 1 - ((24*(Cr2 - Cr1)) / (t*max));
  if (b_1 > 1) {
    b_1 = 1;
  } else if (b_1 < 0) {
    b_1 = 0;
  }

  var kGFR_1 = a_1*b_1;

  kGFR_1 = kGFR_1.toFixed(1);
}

//if (kGFR == 0){
//  kGFR = '~0';
//}

var msg;

if (kGFR <= 30 || kGFR_1 <= 30) {
  msg = "KeGFR of 30 mL/min is 90% specific (71% sensitive) for AKI, according to one small retrospective study (O'Sullivan 2017)";
} else {
  msg = 'KeGFR';
}

//results
if (race == 2 || race == 1) {
calc_output.push({
    name: 'mini',
    value: kGFR,
    value_text: 'mL/min',
    message: 'KeGFR'
});
calc_output.push({
    name: '10191_Kinetic Estimated Glomerular Filtration Rate (keGFR)_result',
    value: kGFR,
    value_text: 'mL/min',
    message: msg  
});
}
if (race == -1) {
calc_output.push({
    name: 'mini',
    value: kGFR_1 + "-" + kGFR,
    value_text: 'mL/min',
    message: 'KeGFR'
});
calc_output.push({
    name: '10191_Kinetic Estimated Glomerular Filtration Rate (keGFR)_result',
    value: kGFR_1 + "-" + kGFR,
    value_text: 'mL/min',
    message: msg + '<br/><br/>Range provided as race not selected'
});
}
