data.logic:
var calc_output = [];
var time1 = 0;
var time2 = parseFloat(time2);
var time3 = parseFloat(time3) || 0;
var time4 = parseFloat(time4) || 0;
var time5 = parseFloat(time5) || 0;
var psa1 = parseFloat(psa1);
var psa2 = parseFloat(psa2);
var psa3 = parseFloat(psa3) || 0;
var psa4 = parseFloat(psa4) || 0;
var psa5 = parseFloat(psa5) || 0;
var add1 = parseFloat(add1);
var add2 = parseFloat(add2) || 0;
var add3 = parseFloat(add3) || 0;

if (UOMSYSTEM == false) {
  time2 = time2*0.03285420945;
  time3 = time3*0.03285420945;
  time4 = time4*0.032854209455;
  time5 = time5*0.03285420945;
}

var x_average, y_average, x1, x2, x3, x4, x5, y1, y2, y3, y4, y5, i1, i2, i3, i4, i5, j1, j2, j3, j4, j5, m, dt;

if (psa5 > 0) {
  psa1 = Math.log(psa1);
  psa2 = Math.log(psa2);
  psa3 = Math.log(psa3);
  psa4 = Math.log(psa4);
  psa5 = Math.log(psa5);

  time3 = time2 + time3;
  time4 = time3 + time4;
  time5 = time4 + time5;

  x_average = (time1 + time2 + time3 + time4 + time5)/5;
  y_average = (psa1 + psa2 + psa3 + psa4 + psa5)/5;

  x5 = time5 - x_average;
  x4 = time4 - x_average;
  x3 = time3 - x_average;
  x2 = time2 - x_average;
  x1 = time1 - x_average;

  y5 = psa5 - y_average;
  y4 = psa4 - y_average;
  y3 = psa3 - y_average;
  y2 = psa2 - y_average;
  y1 = psa1 - y_average;

  i1 = x1*y1;
  i2 = x2*y2;
  i3 = x3*y3;
  i4 = x4*y4;
  i5 = x5*y5;

  j1 = x1*x1;
  j2 = x2*x2;
  j3 = x3*x3;
  j4 = x4*x4;
  j5 = x5*x5;

  m = (i1 + i2 + i3 + i4 + i5)/(j1 + j2 + j3 + j4 +j5);
  dt =  Math.LN2/m;
} 

else if (psa4 > 0) {
  psa1 = Math.log(psa1);
  psa2 = Math.log(psa2);
  psa3 = Math.log(psa3);
  psa4 = Math.log(psa4);

  time3 = time2 + time3;
  time4 = time3 + time4;

  x_average = (time1 + time2 + time3 + time4)/4;
  y_average = (psa1 + psa2 + psa3 + psa4)/4;

  x4 = time4 - x_average;
  x3 = time3 - x_average;
  x2 = time2 - x_average;
  x1 = time1 - x_average;

  y4 = psa4 - y_average;
  y3 = psa3 - y_average;
  y2 = psa2 - y_average;
  y1 = psa1 - y_average;

  i1 = x1*y1;
  i2 = x2*y2;
  i3 = x3*y3;
  i4 = x4*y4;

  j1 = x1*x1;
  j2 = x2*x2;
  j3 = x3*x3;
  j4 = x4*x4;

  m = (i1 + i2 + i3 + i4)/(j1 + j2 + j3 + j4);
  dt =  Math.LN2/m;
}

 else if (psa3 > 0) {
  psa1 = Math.log(psa1);
  psa2 = Math.log(psa2);
  psa3 = Math.log(psa3);

  time3 = time2 + time3;

  x_average = (time1 + time2 + time3)/3;
  y_average = (psa1 + psa2 + psa3)/3;

  x3 = time3 - x_average;
  x2 = time2 - x_average;
  x1 = time1 - x_average;

  y3 = psa3 - y_average;
  y2 = psa2 - y_average;
  y1 = psa1 - y_average;

  i1 = x1*y1;
  i2 = x2*y2;
  i3 = x3*y3;

  j1 = x1*x1;
  j2 = x2*x2;
  j3 = x3*x3;

  m = (i1 + i2 + i3)/(j1 + j2 + j3);
  dt =  Math.LN2/m;
}

 else {
  psa1 = Math.log(psa1);
  psa2 = Math.log(psa2);

  x_average = (time1 + time2)/2;
  y_average = (psa1 + psa2)/2;

  x2 = time2 - x_average;
  x1 = time1 - x_average;

  y2 = psa2 - y_average;
  y1 = psa1 - y_average;

  i1 = x1*y1;
  i2 = x2*y2;

  j1 = x1*x1;
  j2 = x2*x2;

  m = (i1 + i2)/(j1 + j2);
  dt =  Math.LN2/m;
}

if (m <= 0) {
  msg = 'PSA value should be increasing over time';
}

//results
if (m <= 0) {
calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: msg
});
calc_output.push({
    name: '10198_PSA Doubling Time (PSADT) Calculator_result',
    value: '',
    value_text: '',
    message: msg     
});
} else {
  calc_output.push({
    name: 'mini',
    value: dt.toFixed(1),
    value_text: 'months',
    message: 'Doubling time'
});
calc_output.push({
    name: '10198_PSA Doubling Time (PSADT) Calculator_result (slope)',
    value: m.toFixed(1),
    value_text: '',
    message: 'Slope'   
});
calc_output.push({
    name: '10198_PSA Doubling Time (PSADT) Calculator_result (time)',
    value: dt.toFixed(1),
    value_text: 'months',
    message: 'Doubling time'   
});
}
