data.logic:
var calc_output = [];
var guarding = parseFloat(guarding); 
var history = parseFloat(history);     
var rlq = parseFloat(rlq); 
var pain = parseFloat(pain); 
var duration = parseFloat(duration); 
var age1 = parseFloat(age1) || 0;
var age2 = parseFloat(age2) || 0; 
var sex = parseFloat(sex); 
var wbc = parseFloat(wbc); 
var neu = parseFloat(neu) || 0; 

if (sex == 0) {
  var age = age1;
} else {
  var age = age2;
}

if (neu > 0) {
  anc = (neu*wbc)/100;
} else {
  anc = Math.pow((-0.8783 + 1.1008*Math.sqrt(wbc)),2);
}

if (anc < 14) {
  anc = Math.sqrt(anc)*1.7734;
} else {
  anc = 6.6195;
}

var x = -8.6855 + guarding + pain + rlq + history + anc + duration + age + sex;

var score, a;

if (setting == 1) {
  a = '(ED)';
  score = Math.exp(x) / (1 + Math.exp(x));
  score = score*100;
  score = score.toFixed(0);
  var risk1, risk2;
  if (score <= 5) {
      risk1 = 'Ultra low';
      risk2 = 'Outpatient follow up appropriate if PCP evaluation available within 24 hrs; no diagnostic imaging required';
  }  else if (score >= 6 && score <= 15) {
      risk1 = 'Low';
      risk2 = 'Consider ED observation for 6 hrs for serial exams; if improved, ensure outpatient follow-up in 24 hrs; no imaging required';
  }  else if (score >= 16 && score <= 25) {
      risk1 = 'Low-moderate';
      risk2 = 'If patients symptoms <24 hrs, consider observation for 12 hrs; if not improved, obtain ultrasound and repeat CBC; if pain ≥24 hrs of symptoms, obtain ultrasound to evaluate for appendicitis';
  }  else if (score >= 26 && score <= 50) {
      risk1 = 'Moderate';
      risk2 = 'Ultrasound recommended as first line imaging; admit for observation if ultrasound equivocal';
  }  else if (score >= 51 && score <= 75) {
      risk1 = 'Moderate';
      risk2 = 'Ultrasound recommended as first line imaging; CT if ultrasound equivocal';
  }  else if (score >= 76 && score <= 90) {
      risk1 = 'Moderate-high';
      risk2 = 'Consult surgery; consider imaging based on surgery recommendations';
  }  else if (score > 90) {
      risk1 = 'High';
      risk2 = 'Consult surgery; imaging not required';
  }
} else if (setting == 0) {
  a = '(Community)';
  score = Math.exp(-0.615 + 1.1*(x))/(1 + Math.exp(-0.615 + 1.1*(x)));
  score = score*100;
  score = score.toFixed(0);
  var risk1, risk2;
  if (score < 5) {
      risk1 = 'Very low';
      risk2 = '1.4';
  }  else if (score >= 5 && score <= 14) {
      risk1 = 'Low';
      risk2 = '3.0';
  }  else if (score >= 15 && score <= 24) {
      risk1 = 'Intermediate';
      risk2 = '7.7';
  }  else if (score >= 25 && score <= 49) {
      risk1 = 'Intermediate';
      risk2 = '23.9';
  }  else if (score >= 50 && score <= 74) {
      risk1 = 'Intermediate';
      risk2 = '50.0';
  }  else if (score >= 75 && score <= 84) {
      risk1 = 'Intermediate';
      risk2 = '65.3';
  }  else if (score >= 85) {
      risk1 = 'High';
      risk2 = '84.8';
  }
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: '%',
    message: risk1 + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: '%',
    message: 'pARC Score ' + a   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: 'risk',
    message: 'Risk group'
});
if (setting == 1) {
  calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: risk2
});
}
