data.logic:
var calc_output = [];

var weight = parseFloat(weight1)/parseFloat(weight2);
var alb = parseFloat(alb);

if (UOMSYSTEM == true) {
  alb = alb*10;
}

var nri = 1.519*alb + 41.7*weight;

var nri = Math.round(nri * 10) / 10;

var risk, msg;

if (nri > 100) {
  risk = 'No risk';
  msg = '';
} else if (nri >= 97.6 && nri <= 100) {
  risk = 'Mild';
  msg = 'risk';
} else if (nri >= 83.5 && nri <= 97.5) {
  risk = 'Moderate';
  msg = 'risk';
} else if (nri < 83.5) {
  risk = 'Severe';
  msg = 'risk';
}

//results
calc_output.push({
    name: 'mini',
    value: nri,
    value_text: 'points',
    message: risk + ' ' + msg
});
calc_output.push({
    name: '10202_Nutritional Risk Index (NRI)_result',
    value: nri,
    value_text: 'points',
    message: 'NRI Score'   
    });
calc_output.push({
    name: '10202_Nutritional Risk Index (NRI)_interpretation',
    value: risk, 
    value_text: msg,
    message: 'Risk of malnutrition'
});
