var urine = parseFloat(urine);

var msg;
var diag;

if (urine == 1) {
  msg = 'Not low risk';
  diag = '49.6';
} else if (anc == 1) {
  msg = 'Not low risk';
  diag = '6.0';
} else if (pct == 1) {
