data.logic:
var calc_output = [];

var exp = parseFloat(exp1) + parseFloat(exp2) + parseFloat(exp3) + parseFloat(exp4);

var int = parseFloat(int1) + parseFloat(int2) + parseFloat(int3) + parseFloat(int4) + parseFloat(int5);

var av = parseFloat(av1) + parseFloat(av2);

var mo = parseFloat(mo1) + parseFloat(mo2) + parseFloat(mo3) + parseFloat(mo4) + parseFloat(mo5) + parseFloat(mo6) + parseFloat(mo7);

var alt = parseFloat(alt1) + parseFloat(alt2) + parseFloat(alt3) + parseFloat(alt4) + parseFloat(alt5) + parseFloat(alt6);

var req = parseFloat(req1) + parseFloat(req2) + parseFloat(req3);

var diag;

if (exp >=1 && int >= 1 && av >= 1 && mo >= 2 && alt >= 2 && req == 3) {
	diag = 'PTSD';
	msg = 'Diagnosis of PTSD by DSM-5 Criteria';
} else {
	diag = 'Not PTSD';
	msg = 'Diagnosis of PTSD by DSM-5 Criteria';
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: msg   
});
