data.logic:
var calc_output = [];

var stool = parseFloat(stool)

var type = stool + 1

var msg

if (type == 3 || type == 4) {
	msg = 'Normal'
	mini_msg = 'Normal'
}
if (type ==	1 || type == 2) {
	msg = 'Associated with constipation'
	mini_msg = 'Constipation'
}
if (type == 5) {
	msg = 'Associated with diarrhea to some degree'
	mini_msg = 'Diarrhea'
}
if (type == 6 || type == 7) {
	msg = 'Associated with diarrhea'
	mini_msg = 'Diarrhea'
}

//results
calc_output.push({
    name: 'mini',
    value: 'Type ' + type,
    value_text: '',
    message: mini_msg
});
calc_output.push({
    name: 'Score',
    value: 'Type ' + type,
    value_text: '',
    message: msg
});
