data.logic:
var calc_output = [];

var one = parseFloat(one);
var two = parseFloat(two);
var three = parseFloat(three) || 0;
var four = parseFloat(four) || 0;
var five = parseFloat(five) || 0;
var six = parseFloat(six) || 0;
var seven = parseFloat(seven) || 0;
var eight = parseFloat(eight) || 0;
var nine = parseFloat(nine) || 0;
var ten = parseFloat(ten) || 0;

var pd = five + six + seven + eight + nine + ten;
var diag, msg;

if (two == 1) {
  diag = 'sCR';
  msg = 'Stringent complete response by IMWG criteria';
} else if (one == 1) {
  diag = 'CR';
  msg = 'Complete response by IMWG criteria';
} else if (three == 1) {
  diag = 'VGPR';
  msg = 'Very good partial response by IMWG criteria';
} else if (four == 1) {
  diag = 'PR';
  msg = 'Partial response by IMWG criteria';
} else if (pd >= 1) {
  diag = 'PD';
  msg = 'Progressive disease by IMWG criteria';
} else {
  diag = 'SD';
  msg = 'Stable disease by IMWG criteria';
} 

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'diag',
    value: diag,
    value_text: '',
    message: msg
});
