data.logic:
var calc_output = [];

var grading = parseFloat(grading);
var rec;

switch (grading) {
  case 1:
    rec = 'Continue ICPi (or hold temporarily and resume if toxicity remains grade ≤1); monitor for dehydration; recommend dietary changes; GI consult for prolonged cases; see Next Steps for details';
    break;
  case 2:
    rec = 'Hold ICPi until pt recovers to grade 1; consider permanently d/c CTLA-4; may restart PD-1, PD-L1 if pt recovers to grade ≤1; consider steroids; consider loperamide if infection is ruled out; If diarrhea is not transient, start steroids, then taper if symptoms improve to grade ≤1; GI consult; consider EGD/colonoscopy; see Next Steps for details';
    break;
  case 3:
    rec = 'Consider permanently d/c CTLA-4; may restart PD-1, PD-L1 if pt recovers to grade ≤1; start steroids; consider admission or outpatient facility if dehydration/electrolyte imbalance; consider IV steroids or infliximab if persistent/recurrent symptoms; consider colonoscopy if on immunosuppression; see Next Steps for details';
    break;
  case 4:
    rec = 'Permanently d/c ICPis; admit if clinically indicated (monitor closely if outpatient); start steroids and give until symptoms improve, then taper; consider early infliximab if steroid-refractory; consider lower GI endoscopy if refractory symptoms or concern for new infections; see Next Steps for details';
}

//results
calc_output.push({
    name: 'mini',
    value: 'G'+grading,
    value_text: '',
    message: 'Colitis grade'   
});
calc_output.push({
    name: 'Score',
    value: 'G'+grading,
    value_text: '',
    message: 'Colitis grade'   
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: rec
});
