data.logic:
var calc_output = [];
var entry = parseFloat(entry1) + parseFloat(entry2) + parseFloat(entry3);


if (entry == 0) {
  var max = "";
  var msg = "Not AKI according to AKIN";
} else {
  var oliguria = parseFloat(oliguria);
  var creat = parseFloat(creat);
  var max = Math.max(creat, oliguria);
  if (max == 0){
    max = "";
    var msg = "Not AKI according to AKIN";
  } else {
    var msg = "AKIN Classification";
  }
}

//results
if (max != "") {
calc_output.push({
    name: 'mini',
    value: 'Stage ' + max,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: 'Stage ' + max,
    value_text: '',
    message: msg
});
}
if (max == "") {
calc_output.push({
    name: 'mini',
    value: max,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: max,
    value_text: '',
    message: msg
});
}
