data.logic:
var calc_output = [];
var uo = parseFloat(uo);
var gfr = parseFloat(gfr);
var cr = parseFloat(cr);
var arf = parseFloat(arf);
var esrd = parseFloat(esrd);

var grade;
var msg; 

if (esrd == 1) {
  grade = 'ESRD';
  msg = 'RIFLE-E';
} else if (arf == 1) {
  grade = 'Loss';
  msg = 'RIFLE-L';
} else { 
  var max = Math.max(cr, uo, gfr);
  if (max == 1) { 
    grade = 'Risk';
    msg = 'RIFLE-R';
  } else if (max == 2) {
    grade = 'Injury';
    msg = 'RIFLE-I';
  } else if (max == 3) {
    grade = 'Failure';
    msg = 'RIFLE-F/RIFLE-FO if oliguria';
  }
}

//results
calc_output.push({
    name: 'mini',
    value: grade,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: grade,
    value_text: '',
    message: msg
});
