data.logic:
var calc_output = [];

var criteria1 = parseFloat(criteria1) || 0;
var criteria2 = parseFloat(criteria2) || 0;
var criteria3 = parseFloat(criteria3) || 0;
var criteria4 = parseFloat(criteria4) || 0;
var ques1 = parseFloat(ques1) || 0;
var question1 = parseFloat(question1) || 0;
var question2 = parseFloat(question2) || 0;
var question3 = parseFloat(question3) || 0;
var question4 = parseFloat(question4) || 0;

var score = criteria1 + criteria2 + criteria3 + criteria4;
var management, med, level;

if (score == 0) {
	level = '0';
	management = 'Keep patient monitored with pulse oximetry and clinical evaluation.';
	med = 'Lopinavir/ritonavir.';
} else if (score == 1) {
	level = '1';
	management = 'Provide supplemental O<sub>2</sub>. Keep patient monitored with pulse oximetry and clinical evaluation.';
	med = 'Lopinavir/ritonavir.';
} else if (score == 2) {
	level = '2';
	management = 'Perform CXR and ABG. Provide supplemental O<sub>2</sub>. Keep patient monitored with pulse oximetry and clinical evaluation.';
	med = 'Lopinavir/ritonavir. Consider dexamethasone¹.<br/><br/>¹Consider age/comorbidities, cognitive decline.'; 
} else if (score == 3 || score == 4) {
	level = '3';
	management = 'Trial of non-invasive ventilation (CPAP/BiPAP) or high-flow nasal canula (HFNC) recommended. If above clinical criteria worsening or patient clinically worsening despite this trial, intubation recommended. Otherwise, perform CXR every 2 days and ABG twice a day.';
	med = 'Lopinavir/ritonavir. Consider dexamethasone¹ and consider starting tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
	if (ques1 == 2) {
		level = '';
		management = 'Re-select number of criteria present and follow management.';
		med = '';
	} else if (ques1 == 1) {
		if (question1 == 0) {
			level = '4';
			management = 'Follow ICU protocols; use local ventilator weaning protocol.';
			med = 'Remdesivir (if not available, lopinavir/ritonavir) PLUS considering dexamethasone¹ and tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
		} else if (question1 == 1) {
			if (question2 == 1) {
				if (question3 == 0) {
					level = '6';
					management = 'Try to minimize sedation (RASS -1 to 0).';
					med = 'Remdesivir (if not available, lopinavir/ritonavir) PLUS considering dexamethasone¹ and tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
				} else if (question3 == 1) {
					if (question4 == 0) {
						level = '7';
						management = 'Perform best <calculator id="10289">PEEP and Compliance calculation</calculator>. Try to suspend NMBA. Optimize volume status.';
						med = 'Remdesivir (if not available, lopinavir/ritonavir) PLUS considering dexamethasone¹ and tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
					} else if (question4 == 1) {
						level = '8';
						management = 'Extreme level of medical complexity given failing other management options; defer to clinician expertise.';
						med = 'Remdesivir (if not available, lopinavir/ritonavir) PLUS considering dexamethasone¹ and tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
					}
				}
			} else if (question2 == 0) {
				level = '5';
				management = 'Try to minimize sedation (RASS -1 to 0). Perform SBT daily.';
				med = 'Remdesivir (if not available, lopinavir/ritonavir) PLUS considering dexamethasone¹ and tocilizumab².<br/><br/>¹Consider age/comorbidities, cognitive decline.<br/>²<a href="https://www.mdcalc.com/brescia-covid-respiratory-severity-scale-bcrss-algorithm#evidence" target="_blank">Inclusion criteria</a>.';
	    	}
	    }
	}
}

value = 'Level ' + level;
msg = '<strong>Management</strong>: ' + management +'<br/><br/>'+ '<strong>Medications</strong>: ' + med;

if (score > 2 && ques1 == 2) {
	value = '';
	msg = management;
}

//results
calc_output.push({
    name: 'mini',
    value: value,
    value_text: '',
    message: 'Click for management and medication recs.'
});
calc_output.push({
    name: 'Score',
    value: value,
    value_text: '',
    message: msg
});
