data.logic:
var calc_output = [];

var age = parseFloat(age);
var sex = parseFloat(sex);
var loop = parseFloat(loop);
var potassium = parseFloat(potassium);
var qtc = parseFloat(qtc);
var mi = parseFloat(mi);
var sepsis = parseFloat(sepsis);
var hf = parseFloat(hf);
var drugs = parseFloat(drugs);

score =  age + sex + loop + potassium + qtc + mi + sepsis + hf + drugs;
var risk, msg;

if (score < 7) {
    risk = 'Low';
    msg = 'Low risk for QT prolongation; always consider that higher risk may develop depending on clinical course and drug interactions and pharmacokinetics';
} else if (score >= 7 && score <= 10) {
    risk = 'Moderate';
    msg = 'Moderate risk for QT prolongation; consider consultation with pharmacist, adjusting risk factors as much as possible<br/><br/>EKG should be repeated after 5 half-lives of QT prolonging drugs given to evaluate QTc';
} else if (score > 10) {
    risk = 'High';
    msg = 'High risk for QT prolongation; consider consultation with pharmacist, adjusting risk factors as much as possible, and using alternative medications if possible<br/><br/>EKG should be repeated after 5 half-lives of QT prolonging drugs given to evaluate QTc';
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Tisdale Risk Score for QTc'
});
calc_output.push({
    name: 'Score',
    value: risk,
    value_text: 'risk',
    message: msg
});
