var calc_output = [];

req = parseFloat(req1) + parseFloat(req2) + parseFloat(req3);
severity = parseFloat(severity);
risk = parseFloat(risk1);

var diag, msg, level;

if (req == 3 && severity > 0 && risk > 0) {
    diag = 'Positive';
    if (severity == 1) {
        level = 'Mild ARDS';
    } else if (severity == 2) {
        level = 'Moderate ARDS';
    } else if (severity == 3) {
        level = 'Severe ARDS';
    }
    msg = level;
} else {
    diag = 'Not positive';
    msg = 'Not positive for ARDS by Berlin Criteria';
} 

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: msg
});
