data.logic:
var calc_output = [];

var result, msg
if (parseFloat(fullness) + parseFloat(satiation) + parseFloat(pain) + parseFloat(burning) >= 1 && parseFloat(ruleout) == 1) {
  result = 'Positive'
  msg = 'Likely functional dyspepsia'
  }
else {
  result = 'Negative'
  msg = 'Unlikely functional dyspepsia'
  }

var subtype, sub_msg
if (result == 'Positive') {
  if (day3 == 1 && day1 == 1) {
    subtype = 'PDS/EPS'
    sub_msg = 'Both postprandial distress and epigastric pain syndrome subtypes'
  }
  if (day3 == 1 && day1 == 0) {
    subtype = 'PDS'
    sub_msg = 'Postprandial distress syndrome subtype'
  }
  if (day1 == 1 && day3 == 0) {
    subtype = 'EPS'
    sub_msg = 'Epigastric pain syndrome subtype'
  }
}

calc_output.push({
    name: 'mini',
    value: result,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Result',
    value: result,
    value_text: '',
    message: msg + ' (<guideline id = "506">ACG guideline</guideline>)'
});

if (result == 'Positive' && day3 + day1 > 0) {
  calc_output.push({
    name: 'Positive',
    value: subtype,
    value_text: '',
    message: sub_msg
});
}
