data.logic:
var calc_output = [];

var rr = parseFloat(rr);
var fio2 = parseFloat(fio2);
var spo2 = parseFloat(spo2);

var score = (spo2/fio2) / rr;
score = score*100;
score = score.toFixed(2);

var msg;

if (score >= 4.88) {
  msg = 'Low risk of progressing to intubation';
} else if (score < 3.85) {
  msg = 'Risk of HFNC failure is high; intubation should be considered';
} else {
  msg = 'Patient should be reassessed within 2 hours and the index re-calculated';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'ROX Index'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'ROX Index<br/><br/>' + msg
});
