data.logic:
var calc_output = [];
var rate = parseFloat(rate);
var pulse = parseFloat(pulse);
var flow = parseFloat(flow);
var risk;
var level;

var total = rate + pulse + flow;

if (total<=3) {
  level = 'Low';
  risk = '4';
}

if (total>=4 &total<=6) {
  level = 'Low-intermediate';
  risk = '30';
}

if (total>=7 &total<=9) {
  level = 'High-intermediate';
  risk = '44';
}

if (total >=10 &total <=12) {
  level = 'High';
  risk = '57';
}

calc_output.push({
   name: 'mini',
   value: total,
   value_text:'points',
   message: level + ' risk'
});
calc_output.push({
   name: 'qCSI Score',
   value: total,
   value_text:'points',
   message: 'qCSI Score'
});
calc_output.push({
   name: 'qCSI Score',
   value: level,
   value_text: 'risk',
   message: 'Risk group'
});
calc_output.push({
   name: 'qCSI Score',
   value: risk,
   value_text: '%',
   message: 'Risk of critical illness at 24 hrs, defined by oxygen requirement (>10 L/min by low-flow device, high-flow device, non-invasive, or invasive ventilation) or death'
});
