data.logic:
calc_output = [];
var neut = parseFloat(neut);
var lymph = parseFloat(lymph);
var ratio= neut/lymph;
ratio = ratio.toFixed(0);
var level;

if (ratio>=0 & ratio<1) {
  level = '';
} else if (ratio>=1 & ratio<=3) {
  level='Normal';
} else if (ratio>3 & ratio<=5) {
  level = '';
} else if (ratio>=6 & ratio<9) {
  level='Mild'; 
} else if (ratio>=9 & ratio<=18) {
  level='Moderate';
} else if (ratio>18) {
  level='Severe';
}

calc_output.push({
    name: 'mini',
    value: ratio,
    value_text:'',
    message:'Neutrophil-Lymphocyte Ratio'
});
calc_output.push({
    name: '10305_Neutrophil-Lymphocyte Ratio (NLR)_result',
    value: ratio,
    value_text:'',
    message: 'Neutrophil-Lymphocyte Ratio'
});
if(ratio==1 || ratio==2 || ratio==3 || ratio >= 6) {
calc_output.push({
    name: '10305_Neutrophil-Lymphocyte Ratio (NLR)_interpretation',
    value: level,
    value_text:'',
    message:'Physiologic stress level'
});
}
