data.logic:
var calc_output=[];

var criteria1=parseFloat(one)+parseFloat(two)+parseFloat(three)+parseFloat(four);
var criteria2=parseFloat(five);
var criteria3=parseFloat(six)+parseFloat(seven);

var diag1;

if (criteria1>=1) {
  diag1='Positive';
} else {
  diag1='Negative';
}

var msg;
var minimsg;

if (criteria1>=1) {
  if (criteria2==1 & criteria3 !=2) {
    msg='Diagnosis for child functional dyspepsia as per Rome IV criteria<br/><br/>Fulfills additional criteria for child postprandial distress syndrome';
    minimsg = ' diagnosis for child functional dyspepsia. Fulfills additional criteria for child postprandial distress syndrome.';
  }

  else if (criteria2!=1 & criteria3 ==2) {
    msg='Diagnosis for child functional dyspepsia as per Rome IV criteria<br/><br/>Fulfills additional criteria for child epigastric pain syndrome';
    minimsg = ' diagnosis for child functional dyspepsia. Fulfills additional criteria for child epigastric pain syndrome.';
  }

  else if (criteria2==1 & criteria3 ==2) {
    msg='Diagnosis for child functional dyspepsia as per Rome IV criteria<br/><br/>Fulfills additional criteria for child postprandial distress syndrome and child epigastric pain syndrome';
    minimsg = ' diagnosis for child functional dyspepsia. Fulfills additional criteria for child postprandial distress syndrome and child epigastric pain syndrome.';
  }

  else if (criteria2!=1 & criteria3 !=2) {
    msg='Diagnosis for child functional dyspepsia as per Rome IV criteria<br/><br/>Does not fulfill additional criteria for child postprandial distress syndrome or child epigastric pain syndrome';
    minimsg = ' diagnosis for child functional dyspepsia. Does not fulfill additional criteria for child postprandial distress syndrome or child epigastric pain syndrome.';
  }
} else {
  msg='Diagnosis for child functional dyspepsia as per Rome IV criteria';
  minimsg = ' diagnosis for child functional dyspepsia.';
}

calc_output.push({
    name: 'mini',
    value: '',
    value_text:'',
    message:diag1 + minimsg
});
calc_output.push({
    name: 'Rome IV for Child Functional Dyspepsia',
    value: diag1,
    value_text:'',
    message: msg
});
