data.logic:
var calc_output = [];

var grading = parseFloat(grading);
var rec;

switch (grading) {
  case 1:
    rec = 'May continue ICPi with close follow-up; if new onset T2DM consider oral antidiabetics; screen for T1DM if suspected; see Next Steps for details';
    break;
  case 2:
    rec = 'May hold ICPi until serum glucose is controlled; titrate oral therapy or add insulin if worsening of preexisting T2DM; initiate insulin if T1DM is diagnosed or suspected; consult endocrinology if T1DM; consider hospital admission if T1DM and DKA or immediate outpatient evaluation is not available; see Next Steps for details';
    break;
  case 3:
    rec = 'Hold ICPi until pt recovers to Grade ≤1; consult endocrinology; initiate insulin; admit if the patient is symptomatic, DKA development is concerned, or endocrinology is unavailable for patient with T1DM; see Next Steps for details';
    break;
  case 4:
    rec = 'Hold ICPi until pt recovers to Grade ≤1; consult endocrinology; initiate insulin; admit if the patient is symptomatic, DKA development is concerned, or endocrinology is unavailable for patient with T1DM; see Next Steps for details';
}

//results
calc_output.push({
    name: 'mini',
    value: 'G'+grading,
    value_text: '',
    message: 'Hyperglycemia grade'   
});
calc_output.push({
    name: 'Score',
    value: 'G'+grading,
    value_text: '',
    message: 'Hyperglycemia grade'   
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: rec
});
