data.logic:
var calc_output = [];

var grading = parseFloat(grading);
var rec;

switch (grading) {
  case 1:
    rec = 'Continue ICPi with close follow-up, monitor TSH and FT4; see Next Steps for details';
    break;
  case 2:
    rec = 'May hold ICPi until symptoms resolve; consult endocrinology; start thyroid hormone supplementation if symptomatic, or asymptomatic with TSH is persistently > 10 mIU/L; monitor TSH every 6-8 weeks; see Next Steps for details';
    break;
  case 3:
    rec = 'Start thyroid hormone supplementation; Hold ICPi until symptoms resolve with supplementation; consult endocrinology; may admit for IV therapy if signs of myxedema; see Next Steps for details';
    grading = '3-4';

}

//results
calc_output.push({
    name: 'mini',
    value: 'G'+grading,
    value_text: '',
    message: 'Hypothyroidism grade'   
});
calc_output.push({
    name: 'Score',
    value: 'G'+grading,
    value_text: '',
    message: 'Hypothyroidism grade'   
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: rec
});
