data.logic:
var calc_output = [];

var grading = parseFloat(grading);
var rec;

switch (grading) {
  case 1:
    rec = 'Hold ICPi temporarily; repeat CT in 3-4 weeks; consider resuming ICPi if improved/resolved radiographically; monitor weekly with H&P, pulse oximetry and optional CXR; treat as grade 2 if no improvement; see Next Steps for details';
    break;
  case 2:
    rec = 'Hold ICPi until resolution to Grade ≤ 1; start prednisone 1-2 mg/kg ; consider bronchoscopy with BAL; consider empirical antibiotics; monitor every 3 days with H&P, pulse oximetry and optional CXR; treat as Grade 3 if no improvement in 48-72 hours; see Next Steps for details';
    break;
  case 3:
    rec = 'Permanently discontinue ICPi; hospitalize the patient; start empirical antibiotics; start (methyl)prednisolone IV 1-2 mg/kg/d; if no improvement after 48h, consider infliximab, mycophenolate mofetil or IVIG; taper steroids over 4-6w; consult pulmonary and infectious diseases if necessary; offer bronchoscopy with BAL ± transbronchial biopsy; see Next Steps for details';
    break;
  case 4:
    rec = 'Permanently discontinue ICPi; hospitalize the patient; start empirical antibiotics; start (methyl)prednisolone IV 1-2 mg/kg/d; if no improvement after 48h, consider infliximab, mycophenolate mofetil or IVIG; taper steroids over 4-6w; consult pulmonary and infectious diseases if necessary; offer bronchoscopy with BAL ± transbronchial biopsy; see Next Steps for details';
}

//results
calc_output.push({
    name: 'mini',
    value: 'G'+grading,
    value_text: '',
    message: 'Pneumonitis grade'   
});
calc_output.push({
    name: 'Score',
    value: 'G'+grading,
    value_text: '',
    message: 'Pneumonitis grade'   
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: rec
});
