data.logic:
var calc_output = [];

var grading = parseFloat(grading);
var rec;

switch (grading) {
  case 1:
    rec = 'Continue ICPi with close monitoring; consider alternate etiologies; labs 1-2 times weekly; supportive care for symptoms; see Next Steps for details';
    break;
  case 2:
    rec = 'Hold ICPi temporarily; consider prednisone ≤10 mg/d; consider resuming ICPi if pt recovers to Grade 1; consider d/c other hepatotoxic meds; if symptomatic and persistent consider 0.5–1 mg/kg/d prednisone or equivalent; monitor every 3 days; see Next Steps for details';
    break;
  case 3:
    rec = 'Immediately start 1-2 mg/kg methylprednisone or equivalent; if refractory/unresponsive consider MMF or azathioprine; monitor daily or every other day; consider admission if AST/ALT >8 x ULN and/or elevated TB 3 x ULN; consider immunosuppressants; see Next Steps for details';
    break;
  case 4:
    rec = 'Permanently d/c ICPi; start 2 mg/kg/d methylprednisolone equivalent; consider admission; monitor daily; if refractory/unresponsive consider MMF, consult hepatology; avoid infliximab; Consider transfer to tertiary care facility; see Next Steps for details';
}

//results
calc_output.push({
    name: 'mini',
    value: 'G'+grading,
    value_text: '',
    message: 'Hepatitis grade'   
});
calc_output.push({
    name: 'Score',
    value: 'G'+grading,
    value_text: '',
    message: 'Hepatitis grade'   
});
calc_output.push({
    name: 'risk1',
    value: '', 
    value_text: '',
    message: rec
});
