data.logic:
var calc_output=[];

var major =parseFloat(seizure)+parseFloat(onset)+parseFloat(eeg)+parseFloat(impairment);
var minor =parseFloat(medication)+parseFloat(vns)+parseFloat(helmet)+parseFloat(abnormalities);

var diag;

var msg= 'Diagnosis for Lennox-Gastaut Syndrome as per REST-LGS criteria<br/><br/>In a diagnosis-blinded retrospective chart review, application of the REST-LGS tool revealed that a majority of patients with confirmed LGS diagnosis met 3 major criteria and 2-3 minor criteria.';

if (major>=3 && minor>=2) {
  diag='Likely';
} else {
  diag='Unlikely';
}

calc_output.push({
    name: 'mini',
    value: diag,
    value_text:'',
    message:'Diagnosis for Lennox-Gastaut Syndrome'
});
calc_output.push({
    name: 'REST-LGS',
    value: diag,
    value_text:'',
    message:msg
});
