var calc_output = [];

var fever = parseFloat(fever);
var oxygen = parseFloat(oxygen);
var crackles = parseFloat(crackles);
var wheezing = parseFloat(wheezing);

if (UOMSYSTEM == true) {
    var age = parseFloat(age)/12
} else {
    var age = parseFloat(age);
}

var x = 18.05624 + 0.098308*age + 1.144985*fever - 0.21636*oxygen + 0.407494*crackles - 0.907087*wheezing;

var risk = Math.exp(x)/(1 + Math.exp(x)) * 100;

var prs;

if (risk < 5) {
    prs = 1;
} else if (risk < 11) {
    prs = 2;
} else if (risk < 21) {
    prs = 3;
} else if (risk < 51) {
    prs = 4;
} else if (risk < 75) {
    prs = 5;
} else {
    prs = 6;
}

var risk2;
var rec;

if (prs <= 2) {
    risk2 = 'low';
    rec = 'Consider deferring chest X-ray and antibiotics';
}  else if (prs <= 4) {
    risk2 = 'medium';
    rec = 'Consider chest X-ray to further evaluate for pneumonia';
} else {
    risk2 = 'high';
    rec = 'Consider chest X-ray vs. empiric antibiotics';
}

calc_output.push({
    name: 'mini',
    value: risk.toFixed(1),
    value_text: '%',
    message: 'Risk of radiographic pneumonia (' + risk2 + ')'
});
calc_output.push({
    name: 'risk',
    value: risk.toFixed(1),
    value_text: '%',
    message: 'Risk of radiographic pneumonia (' + risk2 + ')'
});
calc_output.push({
