data.logic:
var calc_output=[];

var infant_year = parseFloat(infant_year);
var asymptomatic = parseFloat(asymptomatic);
var explanation = parseFloat(explanation);
var cyanosis = parseFloat(cyanosis);
var absent = parseFloat(absent);
var marked = parseFloat(marked);
var altered = parseFloat(altered);

var age = parseFloat(age);
var similar = parseFloat(similar);
var multiple = parseFloat(multiple);
var history = parseFloat(history);
var premature = parseFloat(premature);

var msg;
var risk;

var sum = cyanosis + absent + marked + altered;

if (sum == 0){
  var sudden = 0;
}
if (sum > 0){
  var sudden = 1;
}

var initial = infant_year + asymptomatic + explanation + sudden;

if (initial < 4){
    msg = "Not BRUE";
    risk = "Entry criteria not fulfilled";

    calc_output.push({
        name: 'mini',
        value: msg,
        value_text: '',
        message: risk
    });
    calc_output.push({
        name: 'Result',
        value: msg,
        value_text: '',
        message: risk
    });
} else if (initial == 4) {
    var x = -3.7545 + 0.00252*age + 0.5644*similar + 0.6457*history;
    var risk_cond = (Math.exp(x)/(1 + Math.exp(x))) * 100;
    var w = -2.4355 + 0.5844*similar + 1.172*multiple + 0.4637*premature + 0.3622*cyanosis - 0.3187*absent - 0.3038*marked;
    var risk_rec = (Math.exp(w)/(1 + Math.exp(w))) * 100;

    calc_output.push({
        name: 'mini',
        value: risk_cond.toFixed(1),
        value_text: '',
        message: '% Risk of serious underlying condition'
    });
    calc_output.push({
        name: 'Risk cond',
        value: risk_cond.toFixed(1),
        value_text: '',
        message: '% Risk of serious underlying condition'
    });
    calc_output.push({
        name: 'Risk rec',
        value: risk_rec.toFixed(1),
        value_text: '',
        message: ' % Risk of recurrent event'
    });
}
