data.logic:
var calc_output=[]; 
var msg=''; 
var mini_msg=''; 
var output_score=parseInt(mechanism)+parseInt(age_12)+parseInt(age_50)+parseInt(bear_weight_unable); 
if(mechanism==0){
    msg='If mechanism is not met, the Pittsburgh Knee Rules cannot be applied.';mini_msg='Rule cannot be applied.'
} else if(mechanism==1){
    if(output_score==1){
        msg='If the only criteria met is \u201cmechanism\u201d, the Pittsburgh Knee Rules suggest knee imaging is not indicated for this patient.';
        mini_msg='Imaging not indicated.'
    }
    if(output_score>1){
        msg='If any of the above criteria are met along with \u201cmechanism\u201d, the Pittsburgh Knee Rules suggest this patient may need knee imaging.';
        mini_msg='May need imaging.'
    }
}
calc_output.push({
    name:'mini',
    value:output_score,
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'1042_Pittsburgh Knee Rules_result',
    value:output_score,
    value_text:'points',
    message:msg
});
