data.logic:
var calc_output = [];

var score = parseFloat(skull) + parseFloat(scalp) + parseFloat(neuro) + parseFloat(alert) + parseFloat(behavior) + parseFloat(coag) + parseFloat(vomit) + parseFloat(age);

var rec;
var risk;

if (score == 0){
    rec = 'CT not necessary';
    risk = 'Low';
} else {
    rec = 'CT necessary';
    risk = 'High';
}

calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk of significant intracranial injuries',
    message: rec
});
calc_output.push({
    name: 'Result',
    value: risk,
    value_text: 'risk of significant intracranial injuries',
    message: rec
});
