data.logic:
var calc_output = [];

var equation = parseFloat(equation);

if (equation == 0) {
    var inr = (parseFloat(inr) < 1) ? 1 : parseFloat(inr);
    var bili = parseFloat(bilirubin);
    var cr = parseFloat(creatinine);

    if (UOMSYSTEM == true) {
        bili = bili;
        cr = cr;
        if (bili < 1) { bili = 1; }
        if (cr < 1) { cr = 1; }
    }
    if (UOMSYSTEM == false) {
        bili = bili / 17.104;
        cr = cr / 88.4; // these are now converted to US units, so now do a check to make sure they're not <1, otherwise set to 1 
        if (bili < 1) { bili = 1; }
        if (cr < 1) { cr = 1; }
    }

    cr = (dialysis == 1 || cr > 4) ? 4 : cr;

    var value = 10 * ((.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + (.957 * Math.log(cr)) + .643);
    value = value.toFixed(0);

    var mort;

    if (value < 6) { 
        value = 6; 
    }

    if (value <= 9) { 
        mort = 1.9; 
    } else if (value <= 19) { 
        mort = 6.0; 
    } else if (value <= 29) { 
        mort = 19.6; 
    } else if (value <= 39) { 
        mort = 52.6; 
    } else if (value >= 40) { 
        mort = 71.3; 
    }

    calc_output.push({ 
        name: 'mini', 
        value: value, 
        value_text: 'points', 
        message: mort + '% estimated 3-month mortality' 
    });
    calc_output.push({ 
        name: 'Original MELD Score', 
        value: value, 
        value_text: 'points', 
        message: 'MELD Score (Original, Pre-2016)' 
    });
    calc_output.push({ 
        name: 'Original MELD Mortality', 
        value: mort, 
        value_text: '%', 
        message: 'Estimated 3-month mortality' 
    });
} else if (equation == 1) {
    var inr = (parseFloat(inr) < 1) ? 1 : parseFloat(inr);
    var na = parseFloat(sodium);

    if (UOMSYSTEM == true) {
        var bili = parseFloat(bilirubin);
        var cr = parseFloat(creatinine);

        if (bili < 1) {
            bili = 1;
        }
        if (cr < 1) {
            cr = 1;
        }
    }

    if (UOMSYSTEM == false) {
        var bili = parseFloat(bilirubin) / 17.104;
        var cr = parseFloat(creatinine) / 88.4;

  // these are now converted to US units, so now do a check to make sure they're not <1, otherwise set to 1

        if (bili < 1) {
            bili = 1;
        }
        if (cr < 1) {
            cr = 1;
        } 
    }

// correct for extremes of na
    if (na < 125) {
        na = 125
    }   
    else if (na > 137) {
        na = 137
    }
    else {
        na = na
    }

// correct for dialysis or high cr
    if (dialysis == 1 || cr > 4) {
        cr = 4
    }
    else {
        cr = cr
    }   
// var meld_i = 10 * ((.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + (.957 *
//   Math.log(cr)) + .643);
    var meld_i = (.957 * Math.log(cr)) + (.378 * Math.log(bili)) + (1.12 * Math.log(inr)) + .643;
    meld_i = 10 * meld_i.toFixed(1)


    var meldnew;

    if (meld_i <= 11) {
        if (meld_i < 6) {
            meldnew = 6;
        }
        else {
            meldnew = meld_i
        }
    }
    if (meld_i > 11) {
        if (meld_i >= 40) {
            var meldnew = 40
        } else {
            var nadiff = 137 - na;
            meldnew = meld_i + (1.32 * nadiff) - (0.033 * meld_i * nadiff);
        }
    } 

    if (meldnew >= 40) {
        meldnew = 40
    }

    var mort;
    if (meldnew.toFixed(0) <= 9) {
        mort = 1.9;
    } else if (meldnew.toFixed(0) >= 10 && meldnew.toFixed(0) < 20) {
        mort = 6.0;
    } else if (meldnew.toFixed(0) >= 20 && meldnew.toFixed(0) < 30) {
        mort = 19.6;
    } else if (meldnew.toFixed(0) >= 30 && meldnew.toFixed(0) < 40) {
        mort = 52.6;
    } else if (meldnew.toFixed(0) >= 40) {
        mort = 71.3;
    }

    calc_output.push({
        name: 'mini',
        value: meldnew.toFixed(0),
        value_text: 'points',
        message: mort + '% estimated 3-month mortality'
    });
    calc_output.push({
        name: 'MELD Score',
        value: meldnew.toFixed(0),
        value_text: 'points',
        message: 'MELD Na (UNOS/OPTN)'
    });
    calc_output.push({
        name: 'MELD Mortality',
        value: mort,
        value_text: '%',
        message: 'Estimated 3-month mortality'
    });
} else if (equation == 2) {
    var inr = parseFloat(inr);
    var sex = parseFloat(sex);
    var sodium = parseFloat(sodium);

    if (UOMSYSTEM == true) { // SI units
        var creatinine = parseFloat(creatinine);
        var bilirubin = parseFloat(bilirubin);
        var albumin = parseFloat(albumin);
    }

    if (UOMSYSTEM == false) { // convert US units
        var creatinine = parseFloat(creatinine)/88.4;
        var bilirubin = parseFloat(bilirubin)/17.104;
        var albumin = parseFloat(albumin)/10;
    }

    if (bilirubin < 1) {
        bilirubin = 1;
    }
    if (inr < 1) {
        inr = 1;
    }
    if (creatinine < 1) {
        creatinine = 1;
    }
    if (creatinine > 3) {
        creatinine = 3;
    }
    if (sodium < 125) {
        sodium = 125;
    }
    if (sodium > 137) {
        sodium = 137;
    }
    if (albumin < 1.5) {
        albumin = 1.5;
    }
    if (albumin > 3.5) {
        albumin = 3.5;
    }

    var score = 1.33*sex + 4.56*Math.log(bilirubin) + 0.82*(137-sodium) - 0.24*(137-sodium)*Math.log(bilirubin) + 9.09*Math.log(inr) + 11.14*Math.log(creatinine) + 1.85*(3.5-albumin) - 1.83*(3.5-albumin)*Math.log(creatinine) + 6;
    score = score.toFixed(0);
    var survival = Math.pow(0.946, Math.exp(0.17698*score - 3.56)) * 100

    calc_output.push({
        name: 'mini',
        value: score,
        value_text: 'points',
        message: survival.toFixed(1) + '% estimated 90-day survival'
    });
    calc_output.push({
        name: 'MELD 3.0',
        value: score,
        value_text: 'points',
        message: 'MELD 3.0'
    });
    calc_output.push({
        name: 'Survival',
        value: survival.toFixed(1),
        value_text: '%',
        message: 'Estimated 90-day survival'
    });
}
