data.logic:
var calc_output = [];

var problems = parseFloat(problems);
var risk = parseFloat(risk);
var ordered = parseFloat(ordered);
var results = parseFloat(results);
var notes = parseFloat(notes);
var hist = parseFloat(hist);
var interp = parseFloat(interp);
var ext = parseFloat(ext);

var p_level;
var p_group;

if (problems == 13) {
    p_level = 2;
    p_group = 'Minimal';
} else if (problems <= 12 && problems >= 8) {
    p_level = 3;
    p_group = 'Low';
} else if (problems <= 7 && problems >= 3) {
    p_level = 4;
    p_group = 'Moderate';
} else {
    p_level = 5;
    p_group = 'High';
}

var r_level;
var r_group;

if (risk == 1) {
    r_level = 2;
    r_group = 'Minimal';
} else if (risk == 2) {
    r_level = 3;
    r_group = 'Low';
} else if (risk == 3) {
    r_level = 4;
    r_group = 'Moderate';
} else {
    r_level = 5;
    r_group = 'High';
}

var d_level;
var d_group;
var sum = ordered + results + notes + hist;
var sum2 = ordered + results + notes;

if ((sum >= 3 && interp == 1) || (sum >= 3 && ext == 1) || (interp == 1 && ext == 1)) {
    d_level = 5;
    d_group = 'Extensive';
} else if (sum >= 3 || interp == 1 || ext == 1) {
    d_level = 4;
    d_group = 'Moderate';
} else if (sum2 == 2 || hist == 1) {
    d_level = 3;
    d_group = 'Limited';
} else if (sum2 <= 1 && hist == 0 && interp == 0 && ext == 0) {
    d_level = 2;
    d_group = 'Minimal';
}

var max2 = [p_level, r_level, d_level].sort(function(a, b) { 
    return b - a; 
})[1];

let full_results_text = '';
let problem_phrases = '';
let problem, risk_text, data, data_default;
problem = `This patient's problem complexity is ${p_group} as patient: `;
risk_text = `This patient's risk is ${r_group} due to: overall presentation requiring evaluation for a potentially ${r_group}-risk process.`;
data = `This patient's data complexity is ${d_group} due to: `;
data_default = `This patient's data complexity is ${d_group}.`;



// problem phrases
switch (problems){
    case 1:
        problem_phrases = 'with chronic illness(es) with severe exacerbation/progression/side effects. ';
        break;
    case 2:
        problem_phrases = 'may have an acute or chronic illness/injury posing a threat to life or body function. ';
        break;
    case 3:
        problem_phrases = 'with chronic illness(es) with exacerbation/progression/side effects of treatment. ';
        break;
    case 4:
        problem_phrases = 'has >1 chronic illnesses that appear to be at their baseline.';
        break;
    case 5:
        problem_phrases = 'has a new undiagnosed problem with uncertain prognosis but that could be serious.';
        break;
    case 6:
        problem_phrases = 'has an acute illness with systemic symptoms.';
        break;
    case 7:
        problem_phrases = 'has an acute complicated injury requiring significant evaluation or with concern for morbidity or multiple treatment options.';
        break;
    case 8:
        problem_phrases = 'has ≥2 minor problems.';
        break;
    case 9:
        problem_phrases = 'has 1 chronic illness that appears to be at its baseline.';
        break;
    case 10:
        problem_phrases = 'has ≥1 acute, uncomplicated illness(es)/injuries.';
        break;
    case 11:
        problem_phrases = 'has 1 acute illness at its baseline/not worsening.';
        break;
    case 12:
        problem_phrases = 'has ≥1 acute, uncomplicated illness(es)/injuries but still needs inpatient/observation-level care.';
        break;
    case 13:
        problem_phrases = 'has 1 self-limited/minor problem.'
        break;
}

// data phrases: 
let data_phrases = '';
if (ordered >= 2 && results < 2){
    data_phrases += '-multiple tests ordered\n';
}
else if (ordered >= 2 && results >= 2){
    data_phrases += '-multiple tests ordered/reviewed\n'
}
if (notes >= 1){
    data_phrases += '-external notes reviewed\n'
}
if (hist == 1){
    data_phrases += '-independent historian used to support history\n'
}
if (interp == 1){
    data_phrases += '-independent interpretation of imaging or EKG\n'
}
if (ext == 1){
    data_phrases += '-discussion of management/testing with external professional'
}

if (data_phrases == ''){
    data = data_default;
}

// add'em up
full_results_text = '\nNARRATIVE MDM:\n' + problem + problem_phrases + '\n' + risk_text + '\n' + data + '\n' + data_phrases;

calc_output.push({
    name: 'mini',
    value: max2,
    value_text: 'Estimated Level of Service',
});
calc_output.push({
    name: 'Level',
    value: max2,
    value_text: 'Estimated Level of Service',
    message: 'Problems: ' + p_group + ' (' + p_level + ') <br/> Risk: ' + r_group + ' (' + r_level + ') <br/> Data: ' + d_group + ' (' + d_level + ')',
    full_results_text: full_results_text,
    button_text: 'Copy MDM'
});
