data.logic:
var calc_output = [];

var therapy = parseFloat(therapy);
var d1 = parseFloat(d1);
var d2 = parseFloat(d2);
var d3 = parseFloat(d3);
var neuro = parseFloat(neuro);

var grade;
var t;
var d;
var n;

if (neuro == 0) {
    grade = 'Grade 1';
}
if (therapy == 1 || d2 == 0 || neuro == 1) {
    grade = 'Grade 2';
}
if (therapy == 2 || d3 == 0) {
    grade = 'Grade 3';
}
if (therapy == 3 || d3 == 1) {
    grade = 'Grade 4';
}
if (therapy == 4) {
    grade = 'Grade 5';
    t = 'T5';
    d = 'D5';
    n = 'N2';
}

if (therapy == 0) {
    t = 'T1';
} else if (therapy == 1) {
    t = 'T2';
} else if (therapy == 2) {
    t = 'T3';
} else if (therapy == 3) {
    t = 'T4';
}

if (d1 == 0) {
    d = 'D1';
} else if (d2 == 0) {
    d = 'D2';
} else if (d3 == 0) {
    d = 'D3';
} else if (d3 == 1) {
    d = 'D4';
}

if (neuro == 0) {
    n = 'N1';
} else if (neuro == 1) {
    n = 'N2';
}

var tdn = t + d + n;

calc_output.push({
    name: 'mini',
    value: grade + ', ' + tdn,
    value_text: '',
    message: 'TDN Grade'
});
calc_output.push({
    name: 'TDN Grade',
    value: grade + ', ' + tdn,
    value_text: '',
    message: 'TDN Grade'
});
