var pneuomnia = parseFloat(pneuomnia)


var score = age + admitneuro + trauma + stroke + cancer + septicemia + noncardiacdx + hepinsuffiiency + admitfromsnf + hypotension + dialysis + respinsuff + pneuomnia

var risk 
var survival


if (score >=24) {
    risk = 'Very low'
    survival = '<1'   
}    
else if (score >= 14 && score <= 23) { 
    risk = 'Low'
