data.logic:
var calc_output = [];

var age = parseFloat(age);
var prior = parseFloat(prior);
var disease = parseFloat(disease);

var rec;
var msg;

if (age == 1) {
    rec = 'OAC';
    msg = 'Therapy recommendation.<br/><br/>Direct oral anticoagulants are preferred over warfarin.';
} else if (age == 0 && prior == 1) {
    rec = 'OAC';
    msg = 'Therapy recommendation.<br/><br/>Direct oral anticoagulants are preferred over warfarin.';
} else if (age == 0 && prior == 0 && disease == 1) {
    rec = 'Antiplatelet therapy';
    msg = 'Therapy recommendation.<br/><br/> Therapeutic options include ASA 81 mg daily alone, clopidogrel 75 mg daily alone, or ASA 81 mg daily in combination with either clopidogrel 75 mg daily, ticagrelor 60 mg bid, or rivaroxaban 2.5 mg bid (depending on clinical circumstance).'
} else {
    rec = 'No antithrombotic';
    msg = 'Therapy recommendation.'
}

calc_output.push({
    name: 'mini',
    value: rec,
    value_text: '',
    message: 'Therapy recommendation'
});
calc_output.push({
    name: 'Rec',
    value: rec,
    value_text: '',
    message: msg
});
