data.logic:
var calc_output = [];

var coombstest = parseFloat(coombstest);
var lowcomplement = parseFloat(lowcomplement);
var antiphospholipid = parseFloat(antiphospholipid);
var antism = parseFloat(antism);
var antidsdna = parseFloat(antidsdna);
var anarange = parseFloat(anarange);
var thrombocytopenia = parseFloat(thrombocytopenia);
var leukopenia = parseFloat(leukopenia);
var hemolyticanemia = parseFloat(hemolyticanemia);
var neurologic = parseFloat(neurologic);
var renal = parseFloat(renal);
var serositis = parseFloat(serositis);
var synovitis = parseFloat(synovitis);
var nonscarringalopecia = parseFloat(nonscarringalopecia);
var oralulcers = parseFloat(oralulcers);
var chroniccutaneous = parseFloat(chroniccutaneous);
var acutecutaneous = parseFloat(acutecutaneous);
var ana = parseFloat(ana);
var biopsy = parseFloat(biopsy);

var diag;
var immuno = coombstest +
lowcomplement +
antiphospholipid +
antism + 
antidsdna + 
anarange;

var clinical = thrombocytopenia +
leukopenia +
hemolyticanemia +
neurologic +
renal +
serositis +
synovitis +
nonscarringalopecia +
oralulcers +
chroniccutaneous + 
acutecutaneous;

if (ana + biopsy == 2) {
	diag = 'Positive';
} else if (immuno + clinical >= 4) {
	if (immuno >= 1 && clinical >= 1) {
		diag = 'Positive';
	} else {
		diag = 'Not positive';
	}
} else {
	diag = 'Not positive';
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: 'Diagnosis by 2012 SLICC Criteria'
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: 'Diagnosis by 2012 SLICC Criteria'   
});
