var calc_output = [];

var weight = parseFloat(weight);

var dose_initial = (weight * 0.25)/5;
var dose_final;

if (dose_initial >= 5){
    dose_final = 5
}
else {
    dose_final = dose_initial
}

var dose_mg = dose_final.toFixed(1)*5;
var waste = 10 - dose_final;
var waste_mg = waste.toFixed(1)*5;

calc_output.push({
    name: 'mini',
    value: dose_final.toFixed(1),
    value_text: 'mL, equivalent to ' + dose_mg.toFixed(1) + ' mg',
    message: 'Tenecteplase dosing'
});
calc_output.push({
    name: '10480_TNK Dosing_result (dose)',
    value: dose_final.toFixed(1),
    value_text: 'mL, equivalent to ' + dose_mg.toFixed(1) + ' mg',
    message: 'Tenecteplase dosing'
});
calc_output.push({
    name: '10480_TNK Dosing_result (waste)',
    value: waste.toFixed(1),
    value_text: 'mL, equivalent to ' + waste_mg.toFixed(1) + ' mg',
    message: 'Waste, to be discarded'
});
