data.logic:
var calc_output = [];

var age = parseFloat(age);
var scd = parseFloat(scd);
var max_wall = parseFloat(max_wall);
var frac_short = parseFloat(frac_short);
var lad = parseFloat(lad);
var lv_outflow = parseFloat(lv_outflow);
var nsvt = parseFloat(nsvt);
var syncope = parseFloat(syncope);

var index = 0.15939858*max_wall - 0.00294271*(Math.pow(max_wall, 2)) + 0.0259082*lad +  0.00446131*lv_outflow + 0.4583082*scd + 0.82639195*nsvt + 0.71650361*syncope - 0.01799934*age;
var risk = (1 - Math.pow(0.998, Math.exp(index)))*100;

calc_output.push({
    name: 'mini',
    value: risk.toFixed(1),
    value_text: '%',
    message: 'Risk of SCD in 5 years'
});
calc_output.push({
    name: '10481_HCM Risk-SCM_result (index)',
    value: index.toFixed(2),
    value_text: '',
    message: 'Prognostic Index'
});
calc_output.push({
    name: '10481_HCM Risk-SCD_result (risk)',
    value: risk.toFixed(1),
    value_text: '%',
    message: 'Risk of SCD in 5 years'
});
