var calc_output = [];

var diagnosis = parseFloat(diagnosis)
var intractability = parseFloat(intractability)
var psychological = parseFloat(psychological)
var chemical = parseFloat(chemical)
var reliability = parseFloat(reliability)
var social = parseFloat(social)
var efficacy = parseFloat(efficacy)

var score = diagnosis + intractability + psychological + chemical + reliability + social + efficacy

var risk

if (score >= 7 && score <= 13) {
	risk = 'NOT a suitable candidate for long-term opioid analgesia'
}
else {
	risk = 'Good candidate for long-term opioid analgesia'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'DIRE Score'
});
calc_output.push({
    name: 'risk',
    value: '',
    value_text: '',
    message: risk
});
