var calc_output = [];

var q1 = parseFloat(q1);
var q2 = parseFloat(q2);
var q3 = parseFloat(q3);
var q4 = parseFloat(q4);

var score = q1 + q2 + q3 + q4;

var tx_eff;
if (score == 0) {
    tx_eff = 'Very poor';
} else if (score <= 5) {
    tx_eff = 'Poor';
} else if (score <= 7) {
    tx_eff = 'Moderate';
} else {
    tx_eff = 'Maximum';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: tx_eff + ' treatment efficacy'
});
