var v, high, low, units, range;
v = 1.5 * parseFloat(bicarbonate) + 8;
v = Math.round(v * 1) / 1;
high = v + 2;
low = v - 2;
if (UOMSYSTEM == false){
  high = high*0.133;
  low = low*0.133
  units = 'kPa'
} else {units = 'mm Hg';}
