data.logic:
var calc_output = [];
var u = parseFloat(penetrating_mechanism) + parseFloat(ed_systolic_bp_90) + parseFloat(ed_hr_120) + parseFloat(positive_fast_exam);
var msg = '';
var mini_msg;

if (u < 2) {
    msg = 'Less likely to require massive transfusion (≥10 units pRBCs).<br/><br/>In the ABC Score studies, scores <2 were unlikely to require massive transfusion, with sensitivity and specificity ranging from 75% to 90% and 67% to 88%, respectively, at multiple trauma centers.';
    mini_msg = 'Less likely to require massive transfusion.';
}
if (u >= 2) {
	msg = 'Likely to require massive transfusion (≥10 units pRBCs).<br/><br/>In the ABC Score studies, scores ≥2 were likely to require massive transfusion, with sensitivity and specificity ranging from 75% to 90% and 67% to 88%, respectively, at multiple trauma centers.';
	mini_msg = 'Likely to require massive transfusion.';
}
calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '1228_ABC Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
