var calc_output=[]; 
var u=parseInt(heart_rate)/parseInt(systolic_blood_pressure); 

calc_output.push({
    name:'mini',
    value:u.toFixed(1),
    value_text:' ',
    message:'Shock Index'
}); 
calc_output.push({
    name:'1316_Shock Index_result',
    value:u.toFixed(1),
    value_text:' ',
    message:'0.5-0.7 is believed to be a normal shock index. Higher numbers have been shown to be more sensitive than vital signs alone in diagnosing occult shock, need for transfusion and/or operation.'
});
