data.logic:
var calc_output = [];
var aniongap = parseFloat(sodium) - parseFloat(chloride) - parseFloat(bicarbonate);
// delta gap

var deltagap = aniongap - 12;
var deltaratio = deltagap / (24 - parseFloat(bicarbonate));
var msg = '';
var units;
var alb = parseFloat(albumin);

if (UOMSYSTEM == false) { // convert albumin to US units
  alb = alb / 10
}

// delta ratio
if (deltaratio < 0.4) {
  msg = 'suggests hyperchloremic normal anion gap acidosis';
}
if (deltaratio >= 0.4 && deltaratio < 1) {
  msg = 'suggests high anion gap and normal anion gap acidosis';
}
if (deltaratio >= 1 && deltaratio <= 2) {
  msg = 'suggests pure anion gap acidosis';
}
if (deltaratio > 2) {
  msg =
    'suggests high anion gap acidosis with concurrent metabolic alkalosis (or compensated respiratory acidosis)';
}

if (UOMSYSTEM == true) { // US units, no change
 units = 'mEq/L';
} else {
  units = 'mmol/L';
}

//Corrected AG
var cag;
var string;
if (alb == 0) {
  cag = 'N/A';
  string = 'Corrected Anion Gap for Albumin';
} else {
  var albCorrect =  (2.5 * (4 - alb));
  cag = aniongap + albCorrect;
  var acdp = cag - 12; 
  var acdr = acdp / (24 - bicarbonate);
if (cag >= 16) {
  string = 'Albumin corrected anion gap; suggests high anion gap acidosis' + '<br/><br/>Albumin corrected delta gap: ' + acdp.toFixed(1) + ' ' + units + '<br/><br/>Albumin corrected delta ratio: ' + acdr.toFixed(1);
} if (cag < 16) {
  string = 'Albumin corrected anion gap; suggests non-anion gap acidosis' + '<br/><br/>Albumin corrected delta gap: ' + acdp.toFixed(1) + ' ' + units + '<br/><br/>Albumin corrected delta ratio: ' + acdr.toFixed(1);
}
}


  calc_output.push({
    name: 'mini',
    value: aniongap.toFixed(1),
    value_text: units,
    message: 'Anion gap'
  });
  calc_output.push({
    name: '1669_Anion Gap_result (gap)',
    value: aniongap.toFixed(1),
    value_text: units,
    message: 'Anion gap' + '<br/><br/>Delta gap: ' +deltagap.toFixed(1) + ' ' + units + '<br/><br/>Delta ratio: ' + deltaratio.toFixed(1) + '; ' +msg 
  });
  if (alb > 0) {
  calc_output.push({
    name: '1669_Anion Gap_result (corrected)',
    value: cag.toFixed(1),
    value_text: units,
    message: string
  });
}
