data.logic:
var calc_output = [];  

var result;
var msg;
var xray;

// malleolar
if (location == 0) {
	result = parseFloat(tender_a) + parseFloat(tender_b) + parseFloat(weightbear)
	xray = 'Ankle x-ray series'
}
// midfoot
if (location == 1) {
	result = parseFloat(tender_c) + parseFloat(tender_d) + parseFloat(weightbear)
	xray = 'Foot x-ray series'
}

if (result > 0) {
	result = 'Cannot rule out'
	msg = ' indicated.'
}
else {
	result = 'Ruled out'
	msg = ' not indicated.'
}

calc_output.push ({
	name:'mini', 
	value: result, 
	value_text: '', 
	message: ''
}); 
calc_output.push ({
	name:'1670_Ottawa Ankle Rule_result', 
	value: result, 
	value_text: '', 
	message: xray + msg
});
