data.logic:
var calc_output = [];

var age = parseFloat(age);
var nihss = parseFloat(nihss);
var ht = parseFloat(ht);
var db = parseFloat(db);
var af = parseFloat(af);
var lvo = parseFloat(lvo);
var aspects = parseFloat(aspects);

var age_s;
if (age <= 59) {
    age_s = 0;
} else if (age < 80) {
    age_s = 1;
} else if (age >= 80) {
    age_s = 2;
}

var nihss_s;
if (nihss <= 10) {
    nihss_s = 0;
} else if (nihss <= 20) {
    nihss_s = 2;
} else if (nihss > 20) {
    nihss_s = 4;
}

var score = age_s + nihss_s + ht + db + af;

var cds = ht + db + af;
var cds1 = 0;
var cds2 = 0;
var cds3 = 0;
if (cds == 1) {
    cds1 = 1;
} else if (cds == 2) {
    cds2 = 1;
} else if (cds == 3) {
    cds3 = 1;
}

if (lvo == 0) {
    var x = 4.942 - 0.035*age - 0.190*nihss - 0.105*cds1 - 0.408*cds2 - 0.702*cds3;
    var prob = 1/(1+Math.exp(-x))*100
    if (prob < 1) {
        prob = prob.toFixed(1);
    } else {
        prob = Math.floor(prob);
    }

    calc_output.push({
        name: 'mini',
        value: score,
        value_text: 'points',
        message: prob + '% chance of good outcome'
    });
    calc_output.push({
        name: '1706_THRIVE Score_result (score)',
        value: score,
        value_text: 'points',
        message: 'THRIVE Score'
    });
    calc_output.push({
        name: '1706_THRIVE Score_result (outcome)',
        value: prob,
        value_text: '%',
        message: 'Chance of good outcome (Modified Rankin 0-2) at 90 days predicted by THRIVE-c'
    });
} else if (lvo == 1) {
    var evt;
    var no_evt;
    if (aspects == -1) {
        evt = 2.864 - 0.025*age - 0.106*nihss - 0.469*cds1 - 0.502*cds2 - 1.665*cds3 + 0.753*1;
        no_evt = 2.864 - 0.025*age - 0.106*nihss - 0.469*cds1 - 0.502*cds2 - 1.665*cds3 + 0.753*0;
    } else if (aspects >=0) {
        evt = 1.828 - 0.031*age - 0.097*nihss - 0.462*cds1 - 0.419*cds2 - 1.798*cds3 + 0.848*1 + 0.153*aspects;
        no_evt = 1.828 - 0.031*age - 0.097*nihss - 0.462*cds1 - 0.419*cds2 - 1.798*cds3 + 0.848*0 + 0.153*aspects;
    }
    var evt_prob = 1/(1+Math.exp(-evt))*100;
    var no_evt_prob = 1/(1+Math.exp(-no_evt))*100;
    if (evt_prob < 1) {
        evt_prob = evt_prob.toFixed(1);
    } else {
        evt_prob = Math.floor(evt_prob);
    }
    if (no_evt_prob < 1) {
        no_evt_prob = no_evt_prob.toFixed(1);
    } else {
        no_evt_prob = Math.floor(no_evt_prob);
    }
    calc_output.push({
        name: 'mini',
        value: score,
        value_text: 'points',
        message: 'THRIVE Score'
    })
    calc_output.push({
        name: '1706_THRIVE Score_result (score)',
        value: score,
        value_text: 'points',
        message: 'THRIVE Score'
    });
    calc_output.push({
        name: '1706_THRIVE Score_result (outcome)',
        value: '',
        value_text: '',
        message: 'Chance of good outcome (Modified Rankin 0-2) at 90 days:<br/><br/>' + evt_prob + '% with endovascular treatment (EVT)<br/>' + no_evt_prob + '% without endovascular treatment (EVT)<br/><br/>Predicted by THRIVE-EVT'
    })
}
