data.logic:
var problems_difficult = problems_difficult || 0;

var calc_output = [];

problems_difficult = parseFloat(problems_difficult);
var v = parseFloat(interest) + parseFloat(depressed) + parseFloat(sleep) + parseFloat(tired) + parseFloat(appetite) + parseFloat(failure) + parseFloat(concentration) + parseFloat(slow) + parseFloat(death);

var text = '';
var mini_msg = '';

if (v <= 4) {
  text = 'Scores â‰¤4 suggest minimal depression which may not require treatment.';
  mini_msg = 'Minimal depression';
}
else if (v > 4 && v < 10) {
  text = 'Scores 5-9 suggest mild depression which may require only watchful waiting and repeated PHQ-9 at followup.';
  mini_msg = 'Mild depression';
}
else if (v >= 10 && v <= 14) {
  text = 'Scores 10-14 suggest moderate depression severity; patients should have a treatment plan ranging form counseling, followup, and/or pharmacotherapy.';
  mini_msg = 'Moderate depression';
}
else if (v > 14 && v <= 19) {
  text = 'Scores 15-19 suggest moderately severe depression; patients typically should have immediate initiation of pharmacotherapy and/or psychotherapy.';
  mini_msg = 'Moderately severe depression';
}
else if (v > 19) {
  text = 'Scores 20 and greater suggest severe depression; patients typically should have immediate initiation of pharmacotherapy and expedited referral to mental health specialist.';
  mini_msg = 'Severe depression';
}

if (problems_difficult == 0) {
  text += '<br/><br/>Functionally, the patient does not report limitations due to their symptoms.';
} else if (problems_difficult == 1) {
  text += '<br/><br/>Functionally, the patient is \u201csomewhat\u201d having difficulty with life tasks due to their symptoms.';
} else if (problems_difficult == 2) {
  text += '<br/><br/>Functionally, the patient finds it is \u201cvery difficult\u201d to perform life tasks due to their symptoms.';
} else if (problems_difficult == 3) {
  text += '<br/><br/>Functionally, the patient finds it is \u201cextremely difficult\u201d to perform life tasks due to their symptoms.';
}

if (parseFloat(death) > 0) {
  text += '<br/><br/>WARNING: This patient is having thoughts concerning for suicidal ideation or self-harm, and should be probed further, referred, or transferred for emergency psychiatric evaluation as clinically appropriate and depending on clinician overall risk assessment.';
}

calc_output.push({
  name: 'mini',
  value: v.toFixed(0),
  value_text: 'points',
  message: mini_msg
});
calc_output.push({
  name: '1725_PHQ-9_result',
  value: v.toFixed(0),
  value_text: 'points',
  message: text
});
